sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var action = new GlideRecord("sys_sysevent_in_email_action"); 
	action.newRecord(); 
	action.name = "LMS: Learning Assignment Bulk Load"; 
	action.sys_scope.setDisplayValue("Global"); 
	action.sys_package.setDisplayValue("Global"); 
	action.table = "sys_data_source"; 
	action.action="record_action"; 
	action.active = true; 
	action.stop_processing = true; 
	action.type = "new"; 
	action.from = gs.getProperty("x_thjuh_lms.service.account.sys_id"); 
	action.description = "Inbound action to create new data source"; 
	action.script = (function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {
		//	current.format = "Excel"; current.header_row = 1; current.sheet_number = 1;  /* -- For Excel file type --*/ 
		var applicatonScope = "LMS"; 
		current.sys_package.setDisplayValue(applicatonScope); 
		current.sys_scope.setDisplayValue(applicatonScope); 
		current.name = "LMS Bulk: " + new GlideDateTime(); 
		current.import_set_table_name = gs.getProperty("x_thjuh_lms.learining.assignment.staging.table"); 
		current.file_retrieval_method = "Attachment"; 
		current.type = "File"; 
		current.format = "CSV"; 
		new x_thjuh_lms.utils().loadImportSet(current.insert(), gs.getProperty("x_thjuh_lms.learning.assignment.transform.maps")); 
	})(current, event, email, logger, classifier); 
	gs.setProperty("x_thjuh_lms.learning.assignment.inbound.action.sys_id", action.insert()); 
	gs.info("Created inbound action for Learining Assignment import via email: " + gs.getProperty("x_thjuh_lms.learning.assignment.inbound.action.sys_id")); 
})(current, previous);
